/*Table structure for table `mordor_albumtag` */

DROP TABLE IF EXISTS `mordor_albumtag`;

CREATE TABLE `mordor_albumtag` (
  `AlbumTagID` int(11) NOT NULL AUTO_INCREMENT,
  `AlbumID` int(11) NOT NULL,
  `TagID` int(11) NOT NULL,
  `CreateTime` datetime NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`AlbumTagID`),
  KEY `AlbumID` (`AlbumID`),
  KEY `TagID` (`TagID`)
) ENGINE=InnoDB AUTO_INCREMENT=111 DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=50;

